import math

class Rectangle:
 def __init__(self, base, height): self.base, self.height = base, height
 def calculatePerimeter(self): return self.base*2 + self.height*2
 def calculateArea(self): return self.base*self.height

class RegularPolygon:
 def __init__(self, number_of_sides, side_length): self.number_of_sides, self.side_length = number_of_sides, side_length
 def findPerimeter(self): return self.number_of_sides * self.side_length
 def findApothem(self): return self.side_length / (2 * math.tan(math.radians((360 / self.number_of_sides) / 2)))
 def findArea(self): return 0.5 * self.findPerimeter() * self.findApothem()

class Circle:
 def __init__(self, radius): self.radius = radius
 def calculateArea(self): return math.pi * (self.radius ** 2)
 def calculatePerimeter(self): return 2 * math.pi * self.radius

class Sphere:
 def __init__(self, radius): self.radius = radius
 def calculateSurface(self): return 4 * math.pi * (self.radius ** 2)
 def calculateVolume(self): return (4/3) * math.pi * (self.radius ** 3)

